["Cast Start"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local spec = GetSpecialization()\n    envTable.spec = spec\n    if envTable.class ~= 9 then\n        envTable.kickId = modTable.kickIndex[envTable.class][envTable.spec]\n    else\n        -- Special warlock logic because warlocks are special\n        if envTable.spec == 1 or envTable.spec == 3 then -- Aff & Destro\n            if IsSpellKnown(19647, true) then -- Felhunter\n                envTable.kickId = 119910\n            else -- Look for Grimoire of Sacrifice\n                local aura = C_UnitAuras.GetPlayerAuraBySpellID(196099)\n                if aura ~= nil then\n                    envTable.kickId = 132409\n                else \n                    envTable.kickId = nil\n                end\n                \n            end\n        elseif spec == 2 then -- Demo\n            if IsSpellKnown(89766, true) then -- Felguard\n                envTable.kickId = 119914\n            elseif IsSpellKnown(19647, true) then -- Felhunter\n                envTable.kickId = 119910\n            else \n                envTable.kickId = nil \n            end\n        end\n    end\n    \n    -- Check for cast\n    local name, _, _, _, _, _, _, notInterruptible = UnitCastingInfo(unitId)\n    if name == nil then    \n        -- Check for channel\n        name, _, _, _, _, _, notInterruptible = UnitChannelInfo(unitId)\n        if name == nil then return end\n    end\n    envTable.notInterruptible = notInterruptible\n    \n    local InterruptCooldown = nil\n    if envTable.kickId ~= nil then\n        InterruptCooldown = C_Spell.GetSpellCooldown(envTable.kickId).duration\n    else\n        InterruptCooldown = 1\n    end\n    \n    if envTable.notInterruptible then\n        --Plater.SetCastBarBorderColor (unitFrame, 1, 0, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"protectedColor\"])\n    elseif InterruptCooldown > 0 then\n        --Plater.SetCastBarBorderColor (unitFrame, 1, 1, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"cooldownColor\"])\n    else\n        --Plater.SetCastBarBorderColor(unitFrame, 0, 1, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"readyColor\"])\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
["Cast Update"] = "function (self, unitId, unitFrame, envTable, modTable)\n    -- Check if the unit is still casting (did someone else interrupt?)\n    local name = UnitCastingInfo(unitId) or UnitChannelInfo(unitId)\n    \n    if self.IsInterrupted then\n        Plater.SetCastBarColor(unitFrame, modTable.config[\"interruptedColor\"])\n        return\n    end\n    \n    local InterruptCooldown = nil\n    if envTable.kickId ~= nil then\n        InterruptCooldown = C_Spell.GetSpellCooldown(envTable.kickId).duration\n    else\n        InterruptCooldown = 1\n    end\n    \n    if envTable.notInterruptible then\n        --Plater.SetCastBarBorderColor (unitFrame, 1, 0, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"protectedColor\"])\n    elseif InterruptCooldown > 0 then\n        --Plater.SetCastBarBorderColor (unitFrame, 1, 1, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"cooldownColor\"])\n    else\n        --Plater.SetCastBarBorderColor(unitFrame, 0, 1, 0)\n        Plater.SetCastBarBorderColor(unitFrame, modTable.config[\"readyColor\"])\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n",
["Enter Combat"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local spec = GetSpecialization()\n    envTable.spec = spec\nend\n\n\n",
